query orders ($after: String, $first: Int, $filter: String, $sort: String, $cultureName: String, $userId: String) {
    orders (after: $after, first: $first, filter: $filter, sort: $sort, cultureName: $cultureName, userId: $userId) {
        items {
            customerId
            customerName
            employeeId
            employeeName
            id
            isApproved
            isCancelled
            shoppingCartId
            status
            storeId
            storeName
            subscriptionId
            subscriptionNumber
            subTotal {
                amount
            }
            subTotalDiscount {
                amount
            }
            total {
                amount
            }
        }
        totalCount
    }
}

query payments ($after: String, $first: Int, $filter: String, $sort: String, $cultureName: String, $userId: String!) {
    payments (after: $after, first: $first, filter: $filter, sort: $sort, cultureName: $cultureName, userId: $userId) {
        items {
            currency {
                code
            }
            customerId
            customerName
            gatewayCode
            id
            number
            paymentMethod {
                code
            }
            purpose
            status
            sum {
                amount
            }
        }
        totalCount
    }
}

