query organizations($searchPhrase: String, $cultureName: String, $sort: String, $after: String, $first: Int){
  organizations (searchPhrase:$searchPhrase, sort: $sort, first: $first, after: $after){ 
    totalCount items{
      name id
      dynamicProperties(cultureName: $cultureName) {
        name value valueType
        dynamicProperty {
          name label valueType
          dictionaryItems(cultureName: $cultureName){ items { label name }}
        }
        dictionaryItem {
          label name id
        }
      }
    } 
  }
}

