graphql.query:
query contacts($searchPhrase: String, $cultureName: String, $sort: String, $after: String, $first: Int){
contacts(searchPhrase:$searchPhrase, sort: $sort, first: $first, after:$after){
totalCount items{
id
name
dynamicProperties(cultureName: $cultureName) {
name value valueType
dynamicProperty {
name label valueType
dictionaryItems(cultureName: $cultureName){ items { label name }}
}
dictionaryItem {
label name id
}
}
}
}
}

