query contact ($id: String!, $userId: String, $cultureName: String) {
    contact (id: $id, userId: $userId) {
        fullName
        dynamicProperties(cultureName: $cultureName){
            dictionaryItem {
                label name
            }
            name value
        }
    }
}

query contacts($filter: String, $sort: String, $cultureName: String, $first: Int, $after: String){
  contacts(filter:$filter, sort: $sort, first: $first, after:$after){ 
    totalCount items{
      id
      name
      dynamicProperties(cultureName: $cultureName) {
        name value valueType
        dynamicProperty {
          name label valueType
          dictionaryItems(cultureName: $cultureName){ items { label name }}
        }
        dictionaryItem {
          label name id
        }
      }
    } 
  }
}

query organizations($filter: String, $cultureName: String, $sort: String, $after: String, $first: Int){
  organizations(filter:$filter, sort: $sort, first: $first, after: $after){ 
    totalCount items{
      name id
      dynamicProperties (cultureName: $cultureName) {
        name value valueType
        dynamicProperty {
          name label valueType
          dictionaryItems(cultureName: $cultureName){ items { label name }}
        }
        dictionaryItem {
          label name id
        }
      }
    } 
  }
}

query role ($roleName: String!) {
    role (roleName: $roleName) {
        description
        id
        name
        normalizedName
        permissions
    }
}

query dynamicProperty ($idOrName: String!, $cultureName: String) {
   dynamicProperty(idOrName: $idOrName, cultureName: $cultureName){
    id
    label
    name
    isArray
    isDictionary
    isMultilingual
    isRequired
    displayOrder
    objectType
    valueType
    dictionaryItems{ 
      totalCount
      items
      { 
        label
        name
      }
    }
  }
}

query dynamicProperties($filter: String!, $cultureName: String, $dictionaryFilter: String, $dictionaryCultureName: String){
  dynamicProperties(filter: $filter, cultureName: $cultureName){
    totalCount
    items{
      objectType
      name isArray label valueType id
      dictionaryItems(filter:$dictionaryFilter cultureName: $dictionaryCultureName ) { 
        totalCount
        items
        { 
          label
          name
        }
      }
    }
  }
}
